SELECT * FROM test_details
where {{ 
  (function() {
      if (Date_filter.formattedDate === "" ) {
        return (status = status_filter.selectedOptionValue);
      }
		else if (status_filter.selectedOptionValue === "") {
        return (deadline <= Date_filter.formattedDate);
      }
		 else {
	 return (deadline <= Date_filter.formattedDate and status = status_filter.selectedOptionValue)
	 }

   })()
}}
order by chapter_tag;

